    public Move Think(Board board, Timer timer)
    {
        var scores = board.GetLegalMoves().Select(move =>
        {
            board.MakeMove(move);
            var score = network.FeedForward(board)[0];

            // #region debug
            //// make score random
            // score = (float)(score + (new Random().NextDouble() - 0.5) * 0.1);
            // DivertedConsole.Write($"score {score}");
            // #endregion

            board.UndoMove(move);
            return (move, score);
        });

        // #region debug
        // DivertedConsole.Write("Scores: " + string.Join(", ", scores.Select(pair => pair.score.ToString("0.000"))));
        // #endregion

        return scores.MaxBy(pair => pair.score).move;
    }

